    private void cmbbukuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cmbbukuActionPerformed
        // TODO add your handling code here:
        String databuku = cmbbuku.getSelectedItem().toString();
        switch(databuku){
            case "Fur Immer Dein Ian":
                DataBuku("Fur Immer Dein Ian",
                        "Mary Simses",
                        "356 Halaman",
                        "Gagas Media",
                        "2018",
                        "Romance"
                );
                break;
            
            case "Arus Balik":
                DataBuku("Arus Balik",
                        "Pramoedya Ananta Toer",
                        "752 Halaman",
                        "Hasta Mitra",
                        "1995",
                        " Fiksi Sejarah"
                );
                break;
                
             case "Bumi Manusia":
                DataBuku("Bumi Manusia",
                        "Pramoedya Ananta Toer",
                        "585 Halaman",
                        "Lentera Dipantara",
                        "1980",
                        " Drama Sejarah"
                );
                break;
                
             case "5 Cm":
                DataBuku("5 Cm",
                        "Donny Dhirgantoro",
                        "38 Halaman",
                        "Grasindo",
                        "2005",
                        " Fiksi Drama"
                );
                break;
                
              case "Negeri 5 Menara":
                DataBuku("Negara 5 Menara",
                        "Ahmad Fuadi",
                        "423 Halaman",
                        "Gramedia",
                        "2009",
                        " Fiksi Drama "
                );
                break;
        }
    }//GEN-LAST:event_cmbbukuActionPerformed

